@Component({
  selector: 'app-basic-list',
  templateUrl: './basic-list.component.html',
  styleUrls: ['./basic-list.component.css']
})
export class BasicListComponent {

}

<p>Lista de elementos:</p>

<p ngClass="underlined">aqui vamos a iterar una lista y mostrar varios elementos</p>

<p [ngClass]="['underlined', 'small']">aqui vamos a iterar una lista y mostrar varios elementos</p>
