import { Component, EventEmitter, Input, OnChanges, OnDestroy, OnInit, Output, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-saludo',
  templateUrl: './saludo.component.html',
  styleUrls: ['./saludo.component.css']
})
export class SaludoComponent implements OnInit, OnDestroy, OnChanges {

  // style for ngStyle
  simon = "#44f";


  // atrapar iformacion desde el padre (props)
  @Input()
  name: string = "juan";

  // pasar informacion al padre
  @Output()
  messageEmitter: EventEmitter<string> = new EventEmitter<string>();

  constructor() {

  }

  // antes de renderizar
  ngOnInit(): void {
    console.log("Me inicie hola (Saludo)");
  }

  // cuando cambie ya renderizado
  ngOnChanges(changes: SimpleChanges): void {
    console.log("Cuando Cambia (Saludo)", changes);
    console.log(`Previous value: ${changes['name'].previousValue}`);
    console.log(`Current value: ${changes['name'].currentValue}`);
  }

  // cuando se destruya
  ngOnDestroy(): void {
    console.log("Cuando se destruye (Saludo)");
  }


  /*
    ejemplo evento click para un evento para enviar un texto al apdre
  */
  sendMessageToFather(): void {
    this.messageEmitter.emit(`Hola. ${this.name}, alerta despachada desde un emiter`);
  }

  /*

  ! ciclo de vida del componente

  * 1.- ngOnChanges
  * 2.- ngOnInit
  3.- ngAfterContentInit
  4.- ngAfterContentChecked
  5.- ngAfterViewInit
  6.- ngAfterViewChecked
  7.- ngAfterContentChecked
  8.- ngAfterViewChecked
  * 9.- ngOnDestroy

  */


}


<h1>Hola, {{ name }}</h1>

<h3 id="usuario" [innerText]="name" >
  <!-- No idicamos nada en la etiqueta si no que el valor viene definico por la propiedad -->
</h3>

<h5 [ngStyle]="{color: simon}">
  Hola que tal pa
</h5>

<button id="emit-alert" (click)="sendMessageToFather()" >enviar mensaje de saludo al padre</button>
